// Validate webhook input for social media publishing
const webhookData = $json.body || $json;

// Debug: Log incoming webhook data
console.log('Social Media Webhook Data:', JSON.stringify(webhookData, null, 2));

// Validate required fields
if (!webhookData.platform) {
  throw new Error('Missing required field: platform');
}
if (!webhookData.content) {
  throw new Error('Missing required field: content');
}

// Validate platform
const supportedPlatforms = ['x', 'twitter', 'facebook', 'linkedin', 'instagram', 'telegram'];
const platform = webhookData.platform.toLowerCase().trim();

if (!supportedPlatforms.includes(platform)) {
  throw new Error(`Unsupported platform: ${platform}. Supported platforms: ${supportedPlatforms.join(', ')}`);
}

// Normalize platform names
let normalizedPlatform = platform;
if (platform === 'twitter') {
  normalizedPlatform = 'x';
}

// Validate content length based on platform
const content = webhookData.content.toString().trim();
if (content.length === 0) {
  throw new Error('Content cannot be empty');
}

// Platform-specific content length validation
const contentLimits = {
  x: 280,
  facebook: 63206,
  linkedin: 3000,
  instagram: 2200,
  telegram: 4096
};

if (content.length > contentLimits[normalizedPlatform]) {
  throw new Error(`Content too long for ${normalizedPlatform}. Max length: ${contentLimits[normalizedPlatform]}, current: ${content.length}`);
}

console.log(`‚úÖ Validated input - Platform: ${normalizedPlatform}, Content length: ${content.length}`);

// Return clean data format
return {
  json: {
    platform: normalizedPlatform,
    content: content,
    originalData: webhookData,
    timestamp: new Date().toISOString(),
    // Extract optional fields
    imageUrl: webhookData.imageUrl || null,
    mediaUrl: webhookData.mediaUrl || null,
    hashtags: webhookData.hashtags || [],
    mentions: webhookData.mentions || [],
    // Platform specific fields
    instagramCaption: webhookData.instagramCaption || content,
    telegramChatId: webhookData.telegramChatId || null
  }
};

// Format success response for X (Twitter)
const result = $json;

console.log('X/Twitter Raw Response:', JSON.stringify(result, null, 2));

// X/Twitter API response format: {data: {id: '1234567890', text: '...'}} or direct {id: '1234567890'}
const postId = result?.data?.id || result?.id || null;
const isSuccess = !result.error && !result.errors && postId;
const errorMsg = result.error?.message || result.errors?.[0]?.title || result.detail || null;

return {
  json: {
    platform: 'x',
    success: isSuccess,
    message: isSuccess ? 'Successfully published on X' : 'Failed to publish on X',
    postId: postId,
    postUrl: postId ? `https://twitter.com/i/web/status/${postId}` : null,
    error: errorMsg,
    rawResponse: result,
    timestamp: new Date().toISOString()
  }
};

// Format success response for Facebook
const result = $json;

console.log('Facebook Raw Response:', JSON.stringify(result, null, 2));

// Facebook Graph API response format: {id: 'page_id_post_id'} or {id: '123456789_987654321'}
const postId = result?.id || null;
const isSuccess = !result.error && postId;
const errorMsg = result.error?.message || result.error?.error_user_msg || null;

// Facebook post URL format
let postUrl = null;
if (postId) {
  if (postId.includes('_')) {
    // Format: page_id_post_id -> facebook.com/page_id/posts/post_id
    const parts = postId.split('_');
    postUrl = `https://facebook.com/${parts[0]}/posts/${parts[1]}`;
  } else {
    // Direct post ID
    postUrl = `https://facebook.com/posts/${postId}`;
  }
}

return {
  json: {
    platform: 'facebook',
    success: isSuccess,
    message: isSuccess ? 'Successfully published on Facebook' : 'Failed to publish on Facebook',
    postId: postId,
    postUrl: postUrl,
    error: errorMsg,
    rawResponse: result,
    timestamp: new Date().toISOString()
  }
};

// Format success response for LinkedIn
const result = $json;

console.log('LinkedIn Raw Response:', JSON.stringify(result, null, 2));

// LinkedIn API response formats:
// Success: {id: 'urn:li:activity:1234567890'} or {urn: 'urn:li:activity:1234567890'} or {activity: 'urn:li:activity:1234567890'}
// n8n LinkedIn node may return different field names
const activityId = result?.id || result?.urn || result?.activity || result?.activityId || null;
const isSuccess = !result.error && !result.errors && activityId;
const errorMsg = result.error?.message || result.errors?.[0]?.message || result.message || null;

// Extract activity ID from URN format
let postId = activityId;
let postUrl = null;
if (activityId) {
  if (activityId.startsWith('urn:li:activity:')) {
    const activityNum = activityId.replace('urn:li:activity:', '');
    postUrl = `https://linkedin.com/feed/update/urn:li:activity:${activityNum}`;
  } else {
    // Direct activity number
    postUrl = `https://linkedin.com/feed/update/urn:li:activity:${activityId}`;
  }
}

return {
  json: {
    platform: 'linkedin',
    success: isSuccess,
    message: isSuccess ? 'Successfully published on LinkedIn' : 'Failed to publish on LinkedIn',
    postId: postId,
    postUrl: postUrl,
    error: errorMsg,
    rawResponse: result,
    timestamp: new Date().toISOString()
  }
};

// Format success response for Instagram
const result = $json;

console.log('Instagram Raw Response:', JSON.stringify(result, null, 2));

// Instagram API response format: {id: 'media_id'} for media creation, then need to publish
// Also check for publish response: {id: 'published_media_id'}
const mediaId = result?.id || null;
const isSuccess = !result.error && mediaId;
const errorMsg = result.error?.message || result.error?.error_user_message || null;

// Instagram media URL - note: actual Instagram post URLs require different format
// For now, using placeholder format since we need actual post shortcode
let postUrl = null;
if (mediaId) {
  // Instagram post URLs need shortcode, not media ID
  // This is a limitation - actual URL would need additional API call
  postUrl = `https://instagram.com/p/${mediaId}`; // This might not work directly
}

return {
  json: {
    platform: 'instagram',
    success: isSuccess,
    message: isSuccess ? 'Successfully created Instagram media' : 'Failed to create Instagram media',
    postId: mediaId,
    postUrl: postUrl,
    error: errorMsg,
    rawResponse: result,
    note: 'Instagram requires 2-step process: create media then publish',
    timestamp: new Date().toISOString()
  }
};

// Format success response for Telegram
const result = $json;

console.log('Telegram Raw Response:', JSON.stringify(result, null, 2));

// Telegram API response format: {message_id: 123, chat: {id: -123456789, type: 'channel'}, text: '...'}
const messageId = result?.message_id || null;
const chatId = result?.chat?.id || null;
const chatUsername = result?.chat?.username || null;
const isSuccess = !result.error && messageId;
const errorMsg = result.error?.description || result.error?.message || null;

// Telegram message URL (for public channels/groups)
let postUrl = null;
if (messageId && chatUsername) {
  postUrl = `https://t.me/${chatUsername}/${messageId}`;
} else if (messageId && chatId) {
  // For private chats, we can't create a public URL
  postUrl = `Telegram message ID: ${messageId} in chat: ${chatId}`;
}

return {
  json: {
    platform: 'telegram',
    success: isSuccess,
    message: isSuccess ? 'Successfully published on Telegram' : 'Failed to publish on Telegram',
    messageId: messageId,
    postId: messageId?.toString() || null,
    chatId: chatId,
    chatUsername: chatUsername,
    postUrl: postUrl,
    error: errorMsg,
    rawResponse: result,
    timestamp: new Date().toISOString()
  }
};

// Platform not supported error
const input = $json;

return {
  json: {
    success: false,
    error: `Platform '${input.platform}' is not supported or routing failed`,
    supportedPlatforms: ['x', 'facebook', 'linkedin', 'instagram', 'telegram'],
    timestamp: new Date().toISOString(),
    input: input
  }
};

// Merge all platform responses
const items = $input.all();

if (items.length === 0) {
  return {
    json: {
      success: false,
      error: 'No platform responses received',
      timestamp: new Date().toISOString()
    }
  };
}

// Take the first (and should be only) response
const response = items[0].json;

return {
  json: {
    ...response,
    webhookResponse: true
  }
};

// Global error handler
const error = $json.error || $json;
const platform = $json.platform || 'unknown';

return {
  json: {
    success: false,
    platform: platform,
    error: error.message || error.toString(),
    errorType: 'validation_error',
    timestamp: new Date().toISOString(),
    supportedPlatforms: ['x', 'facebook', 'linkedin', 'instagram', 'telegram'],
    requestHelp: 'Please check your request format: {"platform": "x|facebook|linkedin|instagram|telegram", "content": "your message"}'
  }
};
